import axios from "axios";

const BASE_URL = "http://localhost:8080/api/medical-records";

const API = "http://localhost:8084/medical-records";

const getToken = () => sessionStorage.getItem("token");

const data = await getAllRecords();
setRecords(Array.isArray(data) ? data : []);


export const searchRecords = async (text) => {
    const res = await fetch(`${API}/search?text=${text}`, {
        headers: {
            Authorization: `Bearer ${getToken()}`
        }
    });

    return res.json();
};

export const filterByDate = async (date) => {
    const res = await fetch(`${API}/date?date=${date}`, {
        headers: {
            Authorization: `Bearer ${getToken()}`
        }
    });

    return res.json();
};
